function initIframeHeight() {
  var iframe = document.getElementById('contentIframe');
  var clientHeight = $('#main-content').height();
  $(iframe).height(clientHeight - 20);
}

window.onload = function () {
  initIframeHeight();
}

window.onresize = function () {
  initIframeHeight();
}