$(function () {
  $('.login_forget>a').on('click', function (e) {
    e.preventDefault();
    $(this).parent().parent().parent('.login_form').addClass('hide');
    $('.forget_pwd_form').removeClass('hide');
  });
  $('.forget_back').on('click', function () {
    $(this).parent().parent('.forget_pwd_form').addClass('hide');
    $('.login_form').removeClass('hide')
  });

  $("#login_form").validate({
    rules: {
      userName: "required",
      pwd: "required",
      userName: {
        required: true,
        minlength: 6
      },
      pwd: {
        required: true,
        minlength: 6
      }
    },
    messages: {
      userName: {
        required: "请输入用户名",
        minlength: "用户名必需由六个字符组成"
      },
      pwd: {
        required: "请输入密码",
        minlength: "密码长度不能小于 6 个字母"
      },
    },
    submitHandler: function () {
      window.location.href = "dashboard.html"
    }
  });
  $("#forget_pwd_form").validate({
    rules: {
      email: {
        required: true,
        email: true
      }
    },
    messages: {
      email: "请输入一个正确的邮箱",
    },
    submitHandler: function () {
      window.location.href = "dashboard.html"
    }
  });
  $('#doLogin').on('click', function () {

  });
  $('#forget_submit').on('click', function () {

  });
});